	padding: 3px;
	border: 1px solid #ccc;
	font-weight: bold !important;
}
#rakutenLimitedId_aroundCart span.choice {
	display: block;
	margin-bottom: 5px;
}



/*お問い合わせボタン*/
font input {
	background:#fcfcfc 10px 5px 10px 0;
	background-image:none;
	margin-bottom:5px;
	padding:5px;
}

