	background: url("http://www.rakuten.ne.jp/gold/kintsu/images/cart04.png") no-repeat ;
    /*border: 1px solid #930;*/
    border: 0;
    height: 45px;
    text-indent: -9999px;
	cursor: pointer !important;
    width: 250px;
	margin-top:10px;
	display:block;
}


