  background-size:cover\9; /* IE9 */
  height: 45px\9;
	width: 250px\9;
}
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
  input[value="買い物かごに入れる"] {
	  background-size:cover;     /* IE10 */
	height: 45px;
	width: 250px;
  }
}

input[value="買い物かごに入れる"]:hover{
	opacity:0.8;
}


/***「お問合せ」ボタン***/
font input[value="商品についての問合わせ"]{
	background: url("http://www.rakuten.ne.jp/gold/kintsu/images/toiawase01.png") no-repeat ;
    /*border: 1px solid #930;*/
    border: 0;
    height: 35px;
    text-indent: -9999px;
	cursor: pointer !important;
    width: 250px;
	margin-top:10px;
}

font input[value="商品についての問合わせ"]:hover{
	opacity:0.7;
}

/***「商品お気に入り」ボタン***/

#rakutenLimitedId_aroundCart div.bkm {
    width: 250px !important;
	height:35px !important;
}

#rakutenLimitedId_aroundCart div.bkm {
    float:none;
    padding-bottom: 0 !important;
}

#rakutenLimitedId_aroundCart div.bookmarkArea div.bkm a.addBkm span.bkmStar {
    background: url("http://www.rakuten.ne.jp/gold/kintsu/images/okini01.jpg") no-repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    display: inline-block !important;
    height: 35px !important;
    margin: 0 0 10px !important;
    width: 250px !important;
}
/***「店舗お気に入り」ボタン***/

#rakutenLimitedId_aroundCart div.shopBkm {
    float: none !important;
    width: 250px !important;
	height:35px !important;
}

#rakutenLimitedId_aroundCart div.shopBkm {
    padding-left: 0 !important;
}

#rakutenLimitedId_aroundCart div.shopBookmarkArea div.shopBkm a.addShopBkm span.shopBkmStar {
    background: url("http://www.rakuten.ne.jp/gold/kintsu/images/okini02.jpg") no-repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    display: inline-block !important;
    height: 35px !important;
    margin-right: 0 !important;
    width: 250px !important;
}




